define(['exports'], function (a) {
  'use strict';
  a.numeric = function () {
    return {
      validate: function (a) {
        if ('' === a.value) return { valid: !0 };
        var t = Object.assign(
            {},
            { decimalSeparator: '.', thousandsSeparator: '' },
            a.options,
          ),
          e = ''.concat(a.value);
        e.substr(0, 1) === t.decimalSeparator
          ? (e = '0'.concat(t.decimalSeparator).concat(e.substr(1)))
          : e.substr(0, 2) === '-'.concat(t.decimalSeparator) &&
            (e = '-0'.concat(t.decimalSeparator).concat(e.substr(2)));
        var r = '.' === t.decimalSeparator ? '\\.' : t.decimalSeparator,
          c = '.' === t.thousandsSeparator ? '\\.' : t.thousandsSeparator,
          n = new RegExp(
            '^-?[0-9]{1,3}('.concat(c, '[0-9]{3})*(').concat(r, '[0-9]+)?$'),
          ),
          o = new RegExp(c, 'g');
        if (!n.test(e)) return { valid: !1 };
        c && (e = e.replace(o, '')), r && (e = e.replace(r, '.'));
        var i = parseFloat(e);
        return { valid: !isNaN(i) && isFinite(i) };
      },
    };
  };
});
